mapper.select:
select 

from basic_manage
where detail_code = #{detailCode,jdbcType=VARCHAR}
  and id = #{id,jdbcType=INTEGER}
  and type_code = #{typeCode,jdbcType=VARCHAR}

mapper.delete:
delete from basic_manage
where detail_code = #{detailCode,jdbcType=VARCHAR}
  and id = #{id,jdbcType=INTEGER}
  and type_code = #{typeCode,jdbcType=VARCHAR}

mapper.insert:
insert into basic_manage (detail_code, id, type_code, 
  detail_nm, max_percent, min_percent, 
  max_amt, min_amt, create_id, 
  create_time, update_id, update_time
  )
values (#{detailCode,jdbcType=VARCHAR}, #{id,jdbcType=INTEGER}, #{typeCode,jdbcType=VARCHAR}, 
  #{detailNm,jdbcType=VARCHAR}, #{maxPercent,jdbcType=DECIMAL}, #{minPercent,jdbcType=DECIMAL}, 
  #{maxAmt,jdbcType=DECIMAL}, #{minAmt,jdbcType=DECIMAL}, #{createId,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateId,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update basic_manage
set detail_nm = #{detailNm,jdbcType=VARCHAR},
  max_percent = #{maxPercent,jdbcType=DECIMAL},
  min_percent = #{minPercent,jdbcType=DECIMAL},
  max_amt = #{maxAmt,jdbcType=DECIMAL},
  min_amt = #{minAmt,jdbcType=DECIMAL},
  create_id = #{createId,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_id = #{updateId,jdbcType=INTEGER},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where detail_code = #{detailCode,jdbcType=VARCHAR}
  and id = #{id,jdbcType=INTEGER}
  and type_code = #{typeCode,jdbcType=VARCHAR}
