mapper.select:
SELECT a.id , a.member_id , b.member_name , b.member_phone , a.status , a.charge_request_time , a.pay_money_type , a.pay_money_time , a.charge_amt , a.charge_apply_time , a.charge_time , a.apply_info , a.remarks , a.create_id , a.create_time , a.update_id , a.update_time FROM member_charge_apply a LEFT JOIN member b ON a.member_id = b.id WHERE 1 = 1
AND a.member_id = #{parameterMap.memberId}
AND b.member_name LIKE concat('%',concat('*',LTrim(RTrim(#{parameterMap.memberName}))),'%') ESCAPE '*'
AND a.status = #{parameterMap.status}
AND DATE_FORMAT(a.charge_request_time, '%Y-%m-%d') >= #{parameterMap.startTime}
AND DATE_FORMAT(a.charge_request_time, '%Y-%m-%d') <= #{parameterMap.endTime}
order by charge_request_time desc

SELECT count(*)
FROM member_charge_apply a
LEFT JOIN member b
ON a.member_id = b.id
WHERE 1 = 1

  AND a.member_id = #{parameterMap.memberId}


  AND b.member_name LIKE concat('%',concat('*',LTrim(RTrim(#{parameterMap.memberName}))),'%') ESCAPE '*'


  AND a.status = #{parameterMap.status}


  AND DATE_FORMAT(a.charge_request_time, '%Y-%m-%d') >= #{parameterMap.startTime}


  AND DATE_FORMAT(a.charge_request_time, '%Y-%m-%d') <= #{parameterMap.endTime}
