sql.where:
    a.order_statues = '2'
and a.sendByPostYN = '2'

    AND a.member_id = #{memberId}


    AND a.order_no LIKE concat('%',concat('*',LTrim(RTrim(#{orderNo}))),'%') ESCAPE '*'


    AND a.order_category = #{orderCategory}


    AND DATE_FORMAT(a.create_time, '%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(a.create_time, '%Y-%m-%d') <= #{endTime}

mapper.select:
SELECT a.id , a.order_no , a.order_category , a.order_amt , a.discount , a.act_amt , b.goods_cd , c.goods_name , b.order_qty , a.express_fee , a.member_id , d.member_name , a.receive_name , a.express_address , a.member_level , a.order_statues , a.create_id , a.create_time , a.update_id , a.update_time , a.recevive_phone FROM order_master a LEFT JOIN order_detail b ON a.order_no = b.order_no LEFT JOIN goods c ON b.goods_cd = c.id LEFT JOIN member d ON a.member_id = d.id
a.order_statues IN ('3','4')
AND a.member_id = #{memberId}
AND a.order_no LIKE concat('%',concat('*',LTrim(RTrim(#{orderNo}))),'%') ESCAPE '*'
AND a.order_category = #{orderCategory}
AND DATE_FORMAT(a.create_time, '%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(a.create_time, '%Y-%m-%d') <= #{endTime}
order by a.id desc

mapper.update:
update order_master
   set order_statues = #{orderStatues,jdbcType=CHAR},
       update_id = #{updateId,jdbcType=INTEGER},
       update_time = #{updateTime,jdbcType=TIMESTAMP}
 where id = #{id,jdbcType=INTEGER}
